(window.webpackJsonp=window.webpackJsonp||[]).push([[35],{191:function(v,_,p){"use strict";p.r(_);var t=p(0),a=Object(t.a)({},function(){var v=this,_=v.$createElement,p=v._self._c||_;return p("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[p("h1",{attrs:{id:"最後の魔法"}},[p("a",{staticClass:"header-anchor",attrs:{href:"#最後の魔法","aria-hidden":"true"}},[v._v("#")]),v._v(" 最後の魔法")]),v._v(" "),p("p",[v._v("「魔術師ネクは引退しました。造水術は出来る方が引き継いでください。さようなら」")]),v._v(" "),p("p",[v._v("このわずか一行のメッセージが王宮を激震させた。造水術は魔術の根幹をなすもののひとつであり，この術がなくてはいかなる水属性魔法も唱えられないのだ。王宮はすぐさま造水術の担い手であった魔術師ネクに連絡を取ろうとしたが，このメッセージとともにネクは消息を絶ってしまった。王宮魔術師たちは，各地で働く魔術師や，貴族お抱えの法術師などから押し寄せる苦情の波に対応せざるを得なくなった。")]),v._v(" "),p("p",[v._v("牛飼いのサイは，友人だったネクの失踪を聞いて心配になった。王宮の魔術師はそんなサイのところへもやってきて，高圧的に居場所を問いただす。おそらく昔の名簿か何かを使って片っ端から問い合わせているのだろう。そんなの知るわけないだろう!そう言って追い返したものの，早くなんとかしなければ，手遅れになってしまうかもしれない。いくつか手がかりはある。過去の記憶と，お高くとまった王宮と，ネクの性格を照らし合わせ，サイは王都の路地裏に向かった。臆病なネクがそれほど遠くへ逃げられるはずもなく，それでいて，決して王宮のやつらが訪れない場所。")]),v._v(" "),p("p",[v._v("崩れかけた建物の壁には雑な補修がなされ，道の端にはあちこちに汚水が溜まって悪臭を放っている。それでいながら，自分を追い越していく子供たちが笑顔なのはどういうわけだろうか。")]),v._v(" "),p("p",[v._v("「また餅玉見せてくれるってよ」")]),v._v(" "),p("p",[v._v("子供の一人が放ったひと言に，サイは眠っていた記憶が音をたててよみがえるような心持ちがした。餅玉は水で作られた軟らかいボールのようなもので，触って遊べる魔法のおもちゃだ。とはいえ，誰でも作れるわけではない。魔力で生み出す水が少なすぎれば球にならず，多すぎれば弾けてしまう。こんな不衛生な場所でも餅玉を作れるのは一人しかいない。")]),v._v(" "),p("p",[v._v("横道に続く階段に，子供たちに囲まれて芸を披露する者の姿があった。ネクだ。ネクは指を回してたやすく水を生み出しては，手の平ほどの大きさに集める。それはつまむと風船のように跳ねるほどやわらかく，それでいてちぎれない見事なバランスだった。")]),v._v(" "),p("p",[v._v("「ネク」")]),v._v(" "),p("p",[v._v("ふっとその呼びかけにネクが顔を上げ，子供たちの隙間にサイの姿を捉えた。と，「わるいの。今日はこれでしまいや」と話すやいなや，きびすを返して走りはじめた。「えー」と残念がる子供の波をかきわけ，サイはネクに呼びかけながらその姿を追った。狭い路地を器用に進むネクを，なんとか視界におさめながら，その姿が消えないよう祈りつつ必死に駆ける。やがてネクのほうが先に疲れたのか，よたよたと走りながら，一つのドアを開けて入ろうとした。")]),v._v(" "),p("p",[v._v("ガッ。サイが足を隙間にひっかける。「ネク，待って。私の話を聞いてくれ」「いやや」「たのむ」「いやや!誰とも話しとうない!」")]),v._v(" "),p("p",[v._v("目を合わせないよう顔を伏せながら，ネクは必死に戸を閉めようとする。その力でドアがメリメリと音をたてる。「待ってくれ。たのむ。このままだと戸が壊れてしまう」「ほならはよ帰りい」「私は王宮の使いで来たわけじゃない。一人で来たんだ」")]),v._v(" "),p("p",[v._v("その言葉にネクの力がゆるむ。「ほんまか」「本当だ」")]),v._v(" "),p("p",[v._v("サイの言葉に，しばらく考えているようだった。長い沈黙をへて，ようやくネクが手を降ろした。「入り」そう言ってネクは暗闇に消えた。サイはその後に続き，ドアを閉めた。緩んだ金具を後で直さなくては。")]),v._v(" "),p("p",[v._v("部屋は薄暗く，かび臭い。天井から焔虫のランプがぶらさがってはいるが，その明かりも弱々しく，とても部屋の隅々を照らすほどではない。ネクは卓のそばにある椅子の埃をはらって，「座り」とうながした。礼を言ってサイが席につこうとすると，「何ももてなしゃせんよ」と言いながら，がしゃがしゃと音をたててネクは食器を探しはじめた。サイは何も言わず，ネクの様子をながめていた。")]),v._v(" "),p("p",[v._v("ネクは水桶から一杯汲み，布で四度漉した。もう一杯汲み，一度漉して，二杯の水を持ってくると，先に用意したほうをサイに差し出した。「飲んだらはよ帰り」")]),v._v(" "),p("p",[v._v("「ありがとう」そう言ってサイはコップを手にとり，飲むそぶりをしてわずかに唇を濡らした。「そうだ，私も持ってきたものがあるんだ」コップを脇によけ，鞄を卓に乗せる。そこから布に包まれた袋をいくつか取り出した。「いらんわ」「そう言わずにさ，食べてよ」「何や」「うちで作った酪だよ」")]),v._v(" "),p("p",[v._v("サイは布を広げた。つやのある白い塊が現れ，香りが鼻から舌へ，酸味を持つものだと伝える。チーズだ。サイはそれを慣れた手つきで切り分けると，卓の中央に布ごと移した。「どうぞ」サイが言いながら一切れ口に入れる。仕方なくネクも一切れ取って端をかじる。知った味だ。間もなくチーズの生きた香りは部屋に充満し，ジメついた空気を一蹴した。まるで殺菌されるようだった。")]),v._v(" "),p("p",[v._v("ネクは首筋をぽりぽりとかく。乾いた肌がこすられる音がした。幼い頃から患う肌の病，良くないと知っていながらついつい触ってしまうクセだった。「今はここに住んでいるのか」サイがたずねた。「ずっとここにおる」「王宮のやつらは来ないのか」「こんなキッタないとこ，よう来ん」")]),v._v(" "),p("p",[v._v("ネクはサイに目を合わせないようにし，そっけない返事をしながらも，チーズを口に運ぶ手は止めない。サイは顔がにやけるのを何とか隠し，平静を装った。")]),v._v(" "),p("p",[v._v("気づかれないよう，ゆっくり視線を部屋の周囲に這わす。見せもんやない，と言われるのが嫌だからだ。すると，流しのそばに，土色の袋が積まれているのが目にとまった。雑な魚の絵が描かれている。")]),v._v(" "),p("p",[v._v("「魚，飼っているのか」")]),v._v(" "),p("p",[v._v("その言葉にようやくネクはサイの顔を見た。だがすぐ目をそらす。「飼ってへん」「じゃああの袋は。餌じゃないのか」「あれはわっちのご飯や」")]),v._v(" "),p("p",[v._v("ふいに頭に手を突っ込まれ，かき回されるような不快感がサイを襲った。これまでの全ての違和感がひとつにつながり，それが導きだす事実に戦慄した。")]),v._v(" "),p("p",[v._v("「ネク」絞るような声で言った。「なんや」「私と暮らそう」")]),v._v(" "),p("p",[v._v("唐突な提案にネクが顔を上げる。「なんや，急に」「私はネクが魔術師になって，王都で暮らしていると思っていた。けれどもこんな」「やめや」「こん，…ここにいたらネクは死んでしまう」「わーめには関係ない」「関係ある」「なんや知り合いづらして，説教か」「私はネクが好きだから」")]),v._v(" "),p("p",[v._v("その一言で時間が止まった。そんな気がした。")]),v._v(" "),p("p",[v._v("言ってしまった，とサイは思った。ネクは視線を落とすと，唇をわずかに動かし，何度かまばたきをする。")]),v._v(" "),p("p",[v._v("どれくらいの沈黙が続いたか。耳元で陣太鼓が叩かれるような鼓動のなか，サイはネクを見つめつづけている。やがてネクは大きくため息をつき，ほんまあほなやっちゃ，とつぶやいた。")]),v._v(" "),p("p",[v._v("「わっちは，わーめが大嫌いや」")]),v._v(" "),p("br"),v._v(" "),p("p",[v._v("ネクが王都を去って十年経った。ある日，干し草を切っているところに，霊蝶がやってきてその髪にとまった。七色のきらめきは王宮からのものだ。ネクは牛の毛繕いをしていたサイを呼んで，家に入った。")]),v._v(" "),p("p",[v._v("紙を取り出して霊蝶をうつすと，じわじわと文字が浮かびあがってくる。「なんて書いてあるんだ？」サイの問いかけにネクは黙っている。")]),v._v(" "),p("p",[v._v("何かを察したサイは，紙を取り上げた。「あっ」わずかのところでネクの手がかすめ，すぐに奪い返そうと必死になる。「あかん，あかんて」高く伸ばしたサイの手をなんとか下ろそうとするそばで，二人のやりとりに気づいた子供たちがわらわらとやってきた。「なんや」「手紙か？」")]),v._v(" "),p("p",[v._v("「オジーを呼んでくれ」そうサイが言ったとたん，「呼んだらあかん!」ネクが怒鳴った。サイは子供たちに手紙を預け，鬼の形相で暴れるネクをなだめつづける。やがてオジー，王都から連れてきた若者の一人，がやってきて，その手に手紙が渡ると，ネクはもうだめだ，と観念した様子でへなへなと力を失ってしまった。")]),v._v(" "),p("p",[v._v("「なんて書いてあるんだ」うなだれるネクを席につかせ，背中をさすりながらサイがたずねる。だが，オジーもしばらく黙っている。ようやく口を開いたが，その声は驚きを含んだものだった。")]),v._v(" "),p("p",[v._v("「ネクは大魔術師さまだったの？」")]),v._v(" "),p("p",[v._v("魔術師，という言葉に子供たちがわきたつ。「ほんま!」「すごい」「ネク魔法使いやったん」そう言いながらネクに抱きつき，「違う，そんなんやない」と言いながらネクが困った様子なのを，なぜかサイは嬉しく思った。")]),v._v(" "),p("p",[v._v("手紙はまぎれもない，王宮からのものだった。そこには，魔術学会の功労賞を受賞したネクに，授賞式への出席を依頼する旨が書かれていた。だが人付き合いが苦手で，しかも逃げるように王都を去ったネクとしては，そんなところに行きたいはずがなかった。")]),v._v(" "),p("p",[v._v("せっかくだから，子供たちにも王都を見せてあげたい。そうサイは言ったが，ネクは「あんなとこおったら心が腐るわ。べべだってこさえな」と渋る。「それなら，子供たちの服をむこうが用意してくれるなら行く？」")]),v._v(" "),p("p",[v._v("「うち都行きたい」「おいも」サイの提案を受け，子供たちがネクの膝の上でせがむ。その様子にネクは眉間を寄せ，顔をかたむけながら考えていたが，覚悟を決めたのか，子供の髪をわしわしとかきながら「ほなら向こうがべべよこすなら行ったるわ」と言った。")]),v._v(" "),p("br"),v._v(" "),p("p",[v._v("宮殿の客室で子供たちの眠る様子をながめながら，ネクはサイに手伝ってもらいつつ，翌日に控えた授賞式での原稿を練習していた。どうしても訛りがぬけず，どもってしまう。ネクは後悔していた。王宮が子供の衣装を送ってきたうえ，家族のためにこんな豪勢な部屋を用意するとは全く思っていなかったからだ。食事も立派なもので，無邪気にほおばる子供やサイの横で，作法を知らぬネクは胃が痛んだ。全ては造水術の担い手であった伝説の大魔術師への期待のあらわれに違いない。買い被りすぎだ。それを思うだけで，これ以上ないほど背中が縮こまり，気にすればするほど，原稿も読めなくなってしまうのだった。今はサイに腰をなでてもらい，多少は落ち着いているけれども，明日は一人で，壇上で，全員の視線を一身にあびることになる。")]),v._v(" "),p("p",[v._v("自ら誘ってしまったがゆえに，サイもネクをこんな状態にさせたことを心苦しく思っていた。「無理しなくていいよ，私が謝るから」「わっちが決めたことや。余計なことせんでええ」")]),v._v(" "),p("p",[v._v("言葉は気丈だが手の震えがひどい。いまこんなに気持ちが高ぶっていては本番でとんでもないことになる。サイはネクの首に顔を寄せた。かつての病はとうに和らぎ，すべらかな肌が唇に触れる。「なんや，こしょぼったい」「もう練習はいいから，寝ようよ」「あほか。こんなん全然足らんわ」「また明日やろうよ。私ももう疲れた。ほら」「どこ触っとんね。やあめ」")]),v._v(" "),p("p",[v._v("サイに促されるまま，ネクもしぶしぶ寝床についた。しばらくは原稿のことが頭から離れなかったが，やがて穏やかな気分に包まれ，夢の世界へと旅立った。")]),v._v(" "),p("br"),v._v(" "),p("p",[v._v("どこまでも高い天井に，王都を代表する知性が集結している。ネクは子供が怯えるほど殺気だち，練習の不足をひたすらサイのせいにしていた。式がはじまるまではサイの影に隠れ，なるべく目立たないようにはしていたものの，礼服を着なれていないサイや子供たちは目立つ。彗星のように現れ，忽然と姿を消した天才魔術師，それが今日来ているという噂が一瞬にして会場中に広まった。")]),v._v(" "),p("p",[v._v("「気い失うかもしれん」「私が起こしてあげるから」「そんな恥ずかしいことんなったらよう生きておれん」「そうなったら誰も知らないところへみんなで行って暮らそう」")]),v._v(" "),p("p",[v._v("サイの励ましもいっこうに効果はなく，結局ネクはサイに抱かれながら，式が始まるまで会場の外で過ごした。頻繁にえずき，子供たちの何人かはネクが本当に死ぬのではないかと心配する。一方のオジーらは皿に並べられたご馳走をたらふく胃に放りこみ，王都の歓迎を満喫していた。")]),v._v(" "),p("br"),v._v(" "),p("p",[v._v("子供たちにとって退屈な式が始まった。サイの隣には，この広い会場でただ一人，爆破や革命などが起きてくれないかと祈り続ける者がある。だがそんなことが起きるはずもなく，式典は滞りなく進み，ついにその時がおとずれてしまった。")]),v._v(" "),p("p",[v._v("「それではかつて造水術を発明し，魔術の世界に大いなる発展をもたらした，ルネル=ネク=ノス様に，受賞のご挨拶をいただきたいと思います」")]),v._v(" "),p("p",[v._v("大きな拍手を受け，立ちあがったネク。だがその足はかたかたと震え，壇上にあがることさえおぼつかない。「若いな」と一人がつぶやいた。「文体からてっきりエルフ族かと思っていたが」他の者がこたえる。皺の寄った顔の並ぶ会場で，ネクたちは異質な存在だった。")]),v._v(" "),p("p",[v._v("壊れた機械のように進んだネクは，ようやく中央に立つと，司会が拡声貝の向きを調整した。衣装のポケットから原稿を取り出す，その紙が震え，まるで見ているほうが緊張するようだった。")]),v._v(" "),p("p",[v._v("「こっ，こんた…この，たびは，わっち，わた，わたくし」")]),v._v(" "),p("p",[v._v("目を原稿に落としたまま，たどたどしく話しはじめるネク。どこからか嘲るような失笑が漏れる。いや，厳かな場面でも吹き出す者が出るだろう，それくらい奇妙な光景である。次第にクスクス笑う声が聞こえてきて，それが波になりつつある。")]),v._v(" "),p("p",[v._v("「…」")]),v._v(" "),p("p",[v._v("のどが凍りついたように動かず，震えがとまらない。蔑まれている空気。着慣れぬ衣装をまとった田舎者。守る者など何もない，裁きの場におかれたような気分に，とうとうネクは沈黙してしまった。瞳に涙を浮かべている。遮るか。司会が介しようとしたそのときだった。")]),v._v(" "),p("p",[v._v("拍手が鳴った。サイだ。オジーたちも続く。何かの遊びと思ったのか，子供たちも元気に手を叩く。その音にネクは顔を上げた。サイと目があう。時には怒ることもあるけれど，優しくて，大好きな，誰よりも大切な人。サイだけではない。ネクを信じて疑わないいくつもの瞳が，まっすぐに見つめている。その様子に，次第に拍手は周りに伝わり，そして会場全体を覆った。")]),v._v(" "),p("p",[v._v("ネクはひとつうなずくと，両の頬を大きく叩いた。その音に拍手がやみ，再びしずまりかえる。原稿をくしゃっと丸めてネクは口を開いた。「こんなかに，わっちの造水術を使ったことある方，おられますか」")]),v._v(" "),p("p",[v._v("一斉に手が上がる。訳せ，と小声で指示を出す者もいる。「おおきに。ほなら，わっちの術やなくてもかまいません，こんなかで，今までに術師に寄付をしたことんある方，どれくらいおられますか」")]),v._v(" "),p("p",[v._v("会場が一瞬にして緊張に包まれた。誰も手を挙げないのだ。")]),v._v(" "),p("p",[v._v("「みなさんが魔術を教えるんとき，どないして精神を扱うかー，は言います。ほやけど，そん術は空気のようなもんやありません。気象や，季節や，土地に合わせて，魔術師が作ったもんです。わっちは水んことしか知りません。おしゃべりもようできません。そんなわっちの作った術が，こん偉い方に使うてもろうて，嬉しゅう思っております。ほやけど，毎年，わっち，寄付をお願いしとりました。そんで，集まったんは，全部集めても，町の宿一泊ほどです。もし，あんとき，ここにいる大事な人，サイがおらんかったら，わっち，ここに立ててへんやろな，と思います。わっち，あほやから，十年前に魔術師やめてしまいました。ほやけど，ひとつだけ，みなさんにお願いがございます。みなさん，今使ってる術が造水術みたいによう使えんようになるんを心配するんやったら，困るんやのうて，困る前に，そん術作ってる魔術師を助けたってください。お願いします」")]),v._v(" "),p("p",[v._v("そう言ってネクは頭を下げ，その拍子に拡声貝にぶつけた。ごっ。という音が会場に響きわたる。額をさするネクに，地面が揺れるほどの大きな歓声があがり，手が破れんばかりの拍手が送られた。")]),v._v(" "),p("p",[v._v("全ての気力を使いはたし，よたよたと壇をおりるネクは，立ち上がって待っていたサイにもたれかかった。何か耳打ちされたサイは，子供たちを促して一緒に会場を出ようとする。")]),v._v(" "),p("p",[v._v("サイは当然のようにネクの勇気をほめたたえたが，それを無視するように顔を伏せたまま，なおもネクは急かす。よほど具合が悪いのか，サイが心配してたずねた。「どうしたの，大丈夫？」")]),v._v(" "),p("p",[v._v("「しょんべんもらした」")]),v._v(" "),p("br"),v._v(" "),p("br"),v._v(" "),p("p",[v._v("– 了 –")]),v._v(" "),p("br"),v._v(" "),p("p",[v._v("この物語はフィクションであり，実在の人物・団体とは一切関係ありません。")]),v._v(" "),p("p",[v._v("(c) 2018 jamcha (jamcha.aa@gmail.com).")]),v._v(" "),p("p",[p("img",{attrs:{src:"https://i.creativecommons.org/l/by-nc-sa/4.0/88x31.png",alt:"cc by-nc-sa"}})])])},[],!1,null,null,null);_.default=a.exports}}]);