(window.webpackJsonp=window.webpackJsonp||[]).push([[44],{193:function(v,_,t){"use strict";t.r(_);var a=t(0),r=Object(a.a)({},function(){var v=this,_=v.$createElement,t=v._self._c||_;return t("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[t("h1",{attrs:{id:"くじらの夢"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#くじらの夢","aria-hidden":"true"}},[v._v("#")]),v._v(" くじらの夢")]),v._v(" "),t("p",[v._v("それは寝苦しい夏の夜のことだった。ふきだす汗の量は敷布団や枕が吸い切れず，寝返りをうつたびに「べちゃ」と水がはりつく音がするほどだ。それでもここ数週間で疲れはててしまった私は，エアコンに手をのばすこともなく「いま風呂に入っていたんだっけ」とおぼろげな感覚で横たわっていた。")]),v._v(" "),t("p",[v._v("身体にはりつく汗の鬱陶しさよりも眠気が勝った私は，やがて遠くでゴォと風がふくのを聞いた気がした。おまけに身体が大きな周期で上下する。この奇妙な身体の感じは，熱を出したときのめまいの様子に近い。ついに熱中症になってしまったか，と妙に冷静な気分なのもおかしな話だが，人間が危険なときにはよくあることだ。")]),v._v(" "),t("p",[v._v("ところが風の音や身体の揺れだけでなく，あの独特な潮の香りが漂ってくるに至って，変なことが起きているな，と気付いた。身体を起こすのも面倒だから，動くところだけで周囲の状況をさぐると，頬が硬いものに触れた。寝返りのあげく床に落ちてしまったか，と考えたがそんな痛みは感じないし，そもそも私はそれほど寝相が悪くないほうだと思う。")]),v._v(" "),t("p",[v._v("身体は上下の動きだけでなく，前方への慣性を感じる。ということは私は寝たまま移動しているということだ。眠ったままの人をマットレスごと湖に流すといういたずらがかつてあったが，都会の真ん中で私にそんな冗談を仕掛ける人がいたものか。")]),v._v(" "),t("p",[v._v("私はいま眠りにつきたくて，それを邪魔しようとする何者かが私を動かしている。そしてそれを確認するために起きるのも面倒に感じている。そこで私はこの問題に決着をつけるべく，これまで身に起きた出来事を総合してそれなりに私の心が耐えられるような考えに至った。というのも仮に海沿いの高速道路を突っ走るトラックの荷台になんか載せられていて，これから見せしめに海に沈められるところだ，なんて事態だったら，私の心は深く傷ついて明日仕事に行きたくない気持ちがさらに増すだろう。")]),v._v(" "),t("p",[v._v("くじらだ。私はくじらの背中に乗って優雅に海を漂っているのだ。イルカ程度の大きさでは私を背中にのせられないし，サメは背びれが邪魔だし仮に背中にのっているなんて怖い。くじらは背びれが立っているわけではないし，島だと思って上陸したらくじらだった，なんて都市伝説もあるくらいだから可能性としては大いにあるではないか。")]),v._v(" "),t("p",[v._v("私はくじらに乗っている。この事実から生じる疑問はふたつある。ひとつは私がなぜくじらに乗っているのか。もうひとつはこのくじらはどこへ向かうのかということだ。くじらに乗っている理由は先述の通りだが，このくじらはどこへ向かっているのだろうか。")]),v._v(" "),t("p",[v._v("いくらくじらが賢いといっても，出会ったばかりの私が話す言葉を理解できるとは思えない。くじらはテレパシーのように会話をするという人もいるが，私は空気が読めなくてこれまで数え切れないほどの損と相手への迷惑をかけてきた。だから仮にくじらが何らかの思念を発したとして，それが私に伝わるとは思えない。考えてみれば，今は海の上なのだ。たとえ目的地がわかったとして私にできることは何もない。私の生死はこのくじらが握っているのだから。")]),v._v(" "),t("p",[v._v("考えをめぐらすごとに，このくじらに対する妙な親近感をおぼえた。これまで私にとってくじらは頭が良い生き物，種類によっては食材にされることもある，程度の知識しかなかった。このくじらや私が夢の世界の住人だったら自由に会話ができただろうに。もどかしさと悔しさがあった。")]),v._v(" "),t("p",[v._v("「これまでテレビでくじらの特集みてもあんまり面白くないな，とか思ってごめん」と私は言った。")]),v._v(" "),t("br"),v._v(" "),t("p",[v._v("「かまわんよ。」")]),v._v(" "),t("br"),v._v(" "),t("br"),v._v(" "),t("p",[v._v("– 了 –")]),v._v(" "),t("br"),v._v(" "),t("br"),v._v(" "),t("p",[v._v("この物語はフィクションであり，実在の人物・団体とは一切関係ありません。")]),v._v(" "),t("p",[v._v("(c) 2016 jamcha (jamcha.aa@gmail.com).")]),v._v(" "),t("p",[t("img",{attrs:{src:"https://i.creativecommons.org/l/by-nc-sa/4.0/88x31.png",alt:"cc by-nc-sa"}})])])},[],!1,null,null,null);_.default=r.exports}}]);